#!/usr/bin/env pythonfrom distutils.core import setupfrom distutils.extension import Extensionfrom Cython.Build import cythonizefrom Cython.Distutils import build_extimport numpy# linux#include_gsl_dir = "/usr/include/gsl"#lib_gsl_dir = "/usr/lib/x86_64-linux-gnu"# mac osinclude_gsl_dir = "/usr/local/Cellar/gsl/2.4/lib"lib_gsl_dir = "/usr/local/Cellar/gsl/2.4/include"setup(    cmdclass = {'build_ext': build_ext},    ext_modules = [Extension("ErCforaging",                             sources=["ErCforaging.pyx", "robot-env.cpp"],                             language="c++",                             include_dirs=[numpy.get_include(), include_gsl_dir],			     libraries=["gsl", "gslcblas"],			     library_dirs=[lib_gsl_dir])],)